import React from 'react';
import { Formik } from 'formik';
import * as yup from 'yup';
import { Form, Col, Row, InputGroup, Button } from 'react-bootstrap';

import './LoginForm.css';

const schema = yup.object({
    username: yup.string().required(),
    password: yup.string().required(),
    terms: yup.bool().required(),
});

class LoginForm extends React.Component{
    testFunction = () => {
        console.log("Yeet");
    }

    render() {
        return (
            <Formik
                validationSchema={schema}
                onSubmit={this.testFunction}
                initialValues={{
                    username: '',
                    password: '',
                }}
            >
                {({
                    handleSubmit,
                    handleChange,
                    handleBlur,
                    values,
                    touched,
                    isValid,
                    errors,
                }) => (
                    <Form noValidate onSubmit={handleSubmit}>
                        <Form.Group className="form-group" as={Row} controlId="test1">
                            <Form.Label> Username </Form.Label>
                            <Form.Control
                                className="form-group"
                                type="text"
                                name="username"
                                value={values.username}
                                onChange={handleChange}
                                isValid={touched.username && !errors.username}
                            />
                        </Form.Group>
                        <Form.Group className="form-group" as={Row} controlId="test2">
                            <Form.Label> Password </Form.Label>
                            <Form.Control
                                className="form-control"
                                type="password"
                                name="password"
                                value={values.password}
                                onChange={handleChange}
                                isValid={touched.password && !errors.password}
                            />
                        </Form.Group>
                        <Button type="submit" className="btn btn-primary btn-block">Submit form</Button>
                    </Form>
                )}
            </Formik>

        )
    }
}

export default LoginForm;
